import React from 'react'
import { Link } from 'react-router-dom'
import dp from '../image/portfolio.png'
import $ from 'jquery'
const Home = () => {
    (function student(){
        var word =[" A Student"," An Engineer"," A Developer"],
        i=0;
        setInterval(function(){
            $('#eng').fadeOut(function(){
                $(this).html(word[i = (i+1)%word.length]).fadeIn();
            });
        },2000)
    })();
  return (
    <>
        <div class="container1 border-0 pb-5"></div>
         <div class="connect">
            <Link to="https://www.linkedin.com/in/satyam-kumar-98387a1ba/" title="linkedin"  target="_blank"><i class="fa fa-linkedin social" style={{color:"#0d69c3" , fontSize:"3rem"}}></i></Link>
            <Link to="https://github.com/Satyam8804" target="_blank" title="github"><i class="fa fa-github social"></i></Link>
            <Link to="https://twitter.com/satyam8804" target="_blank" title="twiter"><i class="fa fa-twitter social" style={{color:"#0d69c3" }} ></i></Link>

        </div>
        <div class="detail">
          <div class="detail2">
            <p>Hi ! I Am </p>
            <span class="color satyam"> Satyam Kumar </span>
            <span id="eng" class="color"  style={{fontSize: "8rem"}}></span>
          </div>
          <div class="btn">
            <button class="btn btn-large fa fa-download button" title="Click to download CV" id="download" type="submit" onclick="window.open('resume.pdf')" style={{fontSize:"small"}} >&nbsp&nbsp Download CV</button>
          </div>
        </div>
        
        <div class="image card1 border-0">
          <div class="lines">
        <img src={dp} alt="img"/>    
            </div> 
         </div>
    </>
  )
}

export default Home
